mapper.select:
select c.id,  c.type_id,  c.role_type,  c.name, c.introduction, c.logo, c.price, c.buy_count, c.web_type,
 c.web_site,  c.collection_field  from crawlers_role as c where c.is_used = 1

     and type_id = #{typeId}


      and web_type = #{webType}

  order by ${orderBy} desc

mapper.update:
update crawlers_role
editor_time = now(), editor_id = #{editorId}, type_id= #{typeId}, role_type = #{roleType}, `name` = #{name}, introduction = #{introduction}, logo = #{logo}, market_price = #{marketPrice}, price = #{price}, `desc` = #{desc}, view_count = IFNULL(download_count,0)+1, buy_count = IFNULL(buy_count,0)+1, file_path = #{filePath}, web_type = #{webType}, web_site = #{webSite}, collection_field = #{collectionField}, is_self_support = #{isSelfSupport}, is_institution = #{isInstitution}, is_guarantee = #{isGuarantee}, is_full_refund = #{isFullRefund}, is_service_supervision = #{isServiceSupervision}, is_great_institution = #{isGreatInstitution}, is_used = #{isUsed},
where id = #{id}
