mapper.select:
SELECT o.id, o.product_type, o.base_id, o.content, o.order_state, o.institution_id, o.addtime, o.editor_time, o.samplePath FROM data_authorize_order as o WHERE user_id = #{userId} AND product_type=#{productType}
AND order_state=#{orderState}
ORDER BY addtime DESC

select t1.transactionOrder,t2.authorizeOrder from
(select count(*) transactionOrder from data_transaction_order where user_id=${userId}) t1,
(select count(*) authorizeOrder from data_authorize_order where user_id=${userId}) t2;
